import * as React from "react"
import { Frame, addPropertyControls, ControlType } from "framer"

export function Jump() {
  // Create an array of letters
  const string = Array.from("First Line")

  // Add staggering effect to the children of the container
  const containerVariants = {
    before: {},
    after: {},
  }

  // Variants for animating each letter
  const letterVariants = {
    before: {},
    after: {},
  }

  return (
    <Frame
      size={"100%"}
      background={
        "linear-gradient(90deg, #0599ff 0%, hsl(204, 100%, 65%) 100%)"
      }
    >
      {
        // Set the display and justifyContent properties to align the text correctly
      }
      <Frame
        center={"y"}
        height={26}
        width={"100%"}
        background={""}
        style={{
          fontFamily: "Montserrat, Work Sans, sans-serif",
          fontWeight: "bold",
          letterSpacing: "-0.04em",
          fontSize: 26,
          color: "#FFF",
          display: "flex", // Set the display value to flex
          justifyContent: "center", // Center all children elements on the x axis
        }}
        variants={containerVariants}
        initial={"before"}
        animate={"after"}
      >
        {string.map((letter, index) => (
          <Frame
            key={index}
            width={"auto"} // Set the width to the width of the letter
            height={26} // Set the height to the height of the text
            background={""}
            style={{ position: "relative" }} // Position elements
            variants={letterVariants}
          >
            {letter}
          </Frame>
        ))}
      </Frame>
    </Frame>
  )
}
